# python decrypt.py output key input_recuperat.txtimport sysf = open(sys.argv[1], "rb")Input = f.read()f.close()Input = Input.decode()key = sys.argv[2]f = open(sys.argv[3], "w")length = len(key)cnt = 0ok = 0for ch in Input:    if ord(ch) != 10 and ok == 1:        x = chr(13 ^ ord(key[(cnt-1+length) % length]))        f.write(x)    elif ok == 1:        cnt = (cnt - 1 + length) % length    ok = 0    x = chr(ord(ch) ^ ord(key[cnt]))    if ord(ch) == 13:        ok = 1    else:        f.write(x)    cnt += 1    if cnt == length:        cnt = 0if ok:    x = chr(13 ^ ord(key[(cnt-1+length) % length]))    f.write(x)f.close()